import sysimport osimport comtypes.clientwdFormatPDF = 17#in_file = input("input docx")#out_file = input("output filename")def docx2pdf(in_file, out_file):    word = comtypes.client.CreateObject('Word.Application')    doc = word.Documents.Open(in_file)    doc.SaveAs(out_file, FileFormat=wdFormatPDF)    doc.Close()    word.Quit()count = 0# traverse root directory, and list directories as dirs and files as filesfor root, dirs, files in os.walk(os.getcwd()):    #print(root)    path = root.split('/')    #print(path)    #print((len(path) - 1) * '---', os.path.basename(root))    #print(files)    for file in files:        #print(len(path) * '---', file)        file_ext = file.split(".")[1]        file_name = file.split(".")[0]        #print(file_ext)        if file_ext == "docx":            count = count + 1            in_file = path[0] + "/" + file            #print(count,":", in_file)            out_file = path[0] + "/" + file_name + ".pdf"            #print(out_file)            docx2pdf(in_file, out_file)#print(count)#docx2pdf(in_file, out_file)print("done")